insert into activities (representative_id, question_id, title, description, sent_at, expire_at, `type`, responses_count, `owner`) select representative_id, id, 'Title', subject, published_at, date_add(published_at, interval 5 day), 'question', (FLOOR( 1 + RAND( ) *60 )), 'a:3:{s:2:"id";i:2;s:14:"official_title";s:4:"test";s:4:"type";s:9:"test_type";}' from poll_questions where representative_id is not null and type='representative';
insert into activities (representative_id, question_id, title, description, sent_at, expire_at, `type`, responses_count, `owner`) select representative_id, id, petition_title, petition_body, published_at, date_add(published_at, interval 5 day), 'petition', (FLOOR( 1 + RAND( ) *60 )), 'a:3:{s:2:"id";i:2;s:14:"official_title";s:4:"test";s:4:"type";s:9:"test_type";}' from poll_questions where representative_id is not null and type='representative_petition';
insert into activity_condition (activity_id, district_id) select id, (select r.district_id from representatives as r where id = a.representative_id) as district_id from activities as a where representative_id IS NOT NULL;
insert into activities (group_id, question_id, title, description, sent_at, expire_at, `type`, responses_count, `owner`) select group_id, id, 'Title', subject, published_at, date_add(published_at, interval 5 day), 'question', (FLOOR( 1 + RAND( ) *60 )), 'a:3:{s:2:"id";i:2;s:14:"official_title";s:4:"test";s:4:"type";s:9:"test_type";}' from poll_questions where group_id is not null and type='group';
insert into activities (group_id, question_id, title, description, sent_at, expire_at, `type`, responses_count, `owner`) select group_id, id, petition_title, petition_body, published_at, date_add(published_at, interval 5 day), 'petition', (FLOOR( 1 + RAND( ) *60 )), 'a:3:{s:2:"id";i:2;s:14:"official_title";s:4:"test";s:4:"type";s:9:"test_type";}' from poll_questions where group_id is not null and type='group_petition';
insert into activity_condition (activity_id, group_id) select id, group_id from activities where group_id is not null;
insert into activities (user_id, petition_id, title, description, sent_at, expire_at, `type`, responses_count, `owner`) select user_id, id, title, petition, created_at, expire_at, 'micro-petition', (FLOOR( 1 + RAND( ) *60 )), 'a:3:{s:2:"id";i:2;s:14:"official_title";s:4:"test";s:4:"type";s:9:"test_type";}' from micropetitions;
insert into activity_condition (activity_id, user_id) select id, user_id from activities where user_id is not null;
